import React, {useState} from 'react'
import { useAuthState } from '../context/auth'
import classNames from 'classnames'
import moment from 'moment'
import { OverlayTrigger, Tooltip } from 'react-bootstrap'

export default function Message({ message }) {

  const {user} = useAuthState()
  const sent = message.from === user.username
  const received = !sent

  return (
    <div className={classNames('d-flex my-3', {
      'ms-auto': sent,
      'me-auto': received
    })}>
    <OverlayTrigger
      placement={'top'}
      overlay={
        <Tooltip>
          {moment(message.createdAt).format('MMM DD, YYYY @ h:mm a')}
        </Tooltip>
      }
      transition={false}>

      <div className={classNames('position-relative px-3 rounded-pill', {
        'bg-primary': sent,
        'bg-light': received
      })}>

        <p className={classNames({'text-white': sent})} key={message.id}>{message.content}</p>
      </div>

      </OverlayTrigger>
    </div>
  )
}
