import React, { createContext, useContext, useReducer } from 'react'

const MessageStateContext = createContext()
const MessageDispatchContext = createContext()

const messageReducer = (state, action) => {
  // let usersCopy, userIndex
  let groupsCopy
  // const { username, message, messages } = action.payload
  switch (action.type) {
    case 'SET_GROUPS':
      return {
        ...state,
        groups: action.payload
      }
    // case 'SET_USER_MESSAGES':
    //   usersCopy = [...state.users]
    //   userIndex = usersCopy.findIndex(u => u.username === username)
    //   usersCopy[userIndex] = {...usersCopy[userIndex], messages}
    //   return {
    //     ...state,
    //     users: usersCopy
    //   }
    case 'SET_SELECTED_GROUP':
      groupsCopy = state.groups.map(group => ({
        ...group,
        selected: group.groupname === action.payload
      }))
      return {
        ...state,
        groups: groupsCopy
      }
    // case 'ADD_MESSAGE':
    //   usersCopy = [...state.users]
    //   userIndex = usersCopy.findIndex(u => u.username === username)
    //   message.reactions = []
    //   let newUser = {
    //     ...usersCopy[userIndex],
    //     messages: usersCopy[userIndex].messages
    //       ? [message, ...usersCopy[userIndex].messages]
    //       : null,
    //     latestMessage: message
    //   }
    //   usersCopy[userIndex] = newUser
    //   return {
    //     ...state,
    //     users: usersCopy
    //   }
  }
}
export const MessageProvider = ({ children }) => {
  const [state, dispatch] = useReducer(messageReducer, { groups: null })
  return (
    <MessageDispatchContext.Provider value={dispatch}>
      <MessageStateContext.Provider value={state}>
        {children}
      </MessageStateContext.Provider>
    </MessageDispatchContext.Provider>
  )
}

export const useMessageState = () => useContext(MessageStateContext)
export const useMessageDispatch = () => useContext(MessageDispatchContext)
