import React, { useState } from 'react'
import { gql, useMutation } from '@apollo/client'
import { Row, Col, Form, Button, InputGroup } from 'react-bootstrap'
import { Link } from 'react-router-dom'

const REGISTER_USER = gql`
  mutation register(
    $username: String!
    $email: String!
    $password: String!
    $confirmPassword: String!
    ){
      register(
        username: $username
        email: $email
        password: $password
        confirmPassword: $confirmPassword
      ){
        username
        email
        createdAt
      }
  }
`
export const Register = (props) => {

  const [credentials, setCredentials] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  })

  const [errors, setErrors] = useState({})

  const [registerUser, { loading }] = useMutation(REGISTER_USER, {

    // Handle error
    onError: (err) => setErrors(err.graphQLErrors[0].extensions.errors),

    // Redirect to login page when registered
    update: () => props.history.push('/login')
  })

  const submitRegistrationForm = (event) => {
    event.preventDefault()
    registerUser({ credentials })
  }

  return (
    <Row className="bg-white rounded-3 py-5 justify-content-center">
      <Col sm={8} md={6} lg={4}>

        {/* HEADING */}
        <h1 className="text-center">REGISTER</h1>

        {/* FORM STARTS */}
        <Form onSubmit={submitRegistrationForm}>

          <Form.Group className="mb-3">
            <Form.Label className={errors.email && 'text-danger'}>
              {errors.email ?? 'Email address'}
            </Form.Label>
            <Form.Control type="email" placeholder="Enter email"
              value={credentials.email} className={errors.email && 'is-invalid'}
              onChange={e => setCredentials({ ...credentials, email: e.target.value })} />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label className={errors.username && 'text-danger'}>
              {errors.username ?? 'Username'}
            </Form.Label>
            <Form.Control type="text" placeholder="Enter user name"
              value={credentials.username} className={errors.username && 'is-invalid'}
              onChange={e => setCredentials({ ...credentials, username: e.target.value })} />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label className={errors.password && 'text-danger'}>
              {errors.password ?? 'Password'}
            </Form.Label>
            <Form.Control type="password" placeholder="Enter password"
              value={credentials.password} className={errors.password && 'is-invalid'}
              onChange={e => setCredentials({ ...credentials, password: e.target.value })} />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label className={errors.confirmPassword && 'text-danger'}>
              {errors.confirmPassword ?? 'Confirm Password'}
            </Form.Label>
            <Form.Control type="password" placeholder="Confirm password"
              value={credentials.confirmPassword} className={errors.confirmPassword && 'is-invalid'}
              onChange={e => setCredentials({ ...credentials, confirmPassword: e.target.value })} />
          </Form.Group>

          <div className="text-center">
            <Button variant="success" type="submit" disabled={loading}>
              {loading ? 'loading..' : 'Register'}
            </Button>
            <br />
            <small>Already have an account ? <Link to="/login">Login</Link></small>
          </div>

        </Form>
        {/* FORM ENDS */}

      </Col>
    </Row>
  )
}
