import React, { useState } from 'react'
import { gql, useMutation } from '@apollo/client'
import { Row, Col, Form, Button, InputGroup } from 'react-bootstrap'
import { Link } from 'react-router-dom'

const REGISTER_USER = gql`
  mutation register(
    $username: String!
    $email: String!
    $password: String!
    $confirmPassword: String!
    ){
      register(
        username: $username
        email: $email
        password: $password
        confirmPassword: $confirmPassword
      ){
        username
        email
        createdAt
      }
  }
`
export const Register = (props) => {

  const [credentials, setCredentials] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  })

  const [errors, setErrors] = useState({})

  const [registerUser, { loading }] = useMutation(REGISTER_USER, {

    // Handle error
    onError: (err) => setErrors(err.graphQLErrors[0].extensions.errors),

    // Redirect to login page when registered
    update: () => props.history.push('/login')
  })

  const submitRegistrationForm = (event) => {
    event.preventDefault()
    registerUser({ credentials })
  }

  return (
    <Row>
      <Col>

        {/* HEADING */}
        <h1>REGISTER</h1>

        {/* FORM STARTS */}
        <Form onSubmit={submitRegistrationForm}>

          <Form.Group className="mb-3" hasValidation>
            {/* <InputGroup > */}
              <Form.Control
                type="email"
                placeholder="Email address"
                className={errors.email && 'is-invalid'}
                value={credentials.email}
                onChange={ (e) => setCredentials({ ...credentials, email: e.target.value }) }
              />
              <Form.Control.Feedback type={errors.email && "invalid"}>
                { errors.email }
              </Form.Control.Feedback>
            {/* </InputGroup>   */}
          </Form.Group>

          <Form.Group>
              <Form.Control
                type="text"
                placeholder="Username"
                className={errors.username && 'is-invalid'}
                value={credentials.username}
                onChange={ (e) => setCredentials({ ...credentials, username: e.target.value }) }
              />
            <Form.Control.Feedback type={errors.username && "invalid"}>
              { errors.email }
            </Form.Control.Feedback>          
          </Form.Group>

        </Form>
        {/* FORM ENDS */}

      </Col>
    </Row>
  )
}
