import React, { useEffect, Fragment, useState } from 'react'
import { Col, Form, Button } from 'react-bootstrap'
import { gql, useLazyQuery } from '@apollo/client'
import { useMessageDispatch, useMessageState } from '../context/message'
import Message from './Message'
import { Home } from './Home'
const GET_MESSAGES = gql`
  query getMessages($to: String!){
    getMessages(to: $to){
      content from to createdAt
    }
  }
`

export const Chatroom = ({ clicked }) => {
  const { groups } = useMessageState()
  const dispatch = useMessageDispatch()
  const [content, setContent] = useState('')

  const selectedGroup = groups?.find(group => group.selected === true)
  console.log('groups: ', groups)
  // const gname = queryString.parse(window.location.search).group

  // const selectedGroup = groups?.find(group => group.groupname === gname)
  console.log('selected group : ', selectedGroup)
  const messages = selectedGroup?.messages
  console.log('messages: ', messages)
  const [getMessages, { loading: messagesLoading, data: messagesData }] = useLazyQuery(GET_MESSAGES)

  useEffect(() => {
    if (selectedGroup && !selectedGroup.messages) {
      getMessages({ variables: { to: selectedGroup.groupname } })
    }
  }, [selectedGroup])

  useEffect(() => {
    if (messagesData) {
      console.log('messages Data : ', messagesData)
      dispatch({
        type: 'SET_GROUP_MESSAGES', payload: {
          groupname: selectedGroup.groupname,
          messages: messagesData.getMessages
      } })
    }
  },[messagesData])

  const submitMessage = (e) => {
    e.preventDefault()
    if (content.trim() === '' || !selectedGroup) return
    console.log('send message: ', content)
    setContent('')
    //mutation for sending a message
    // sendMessage({
    //   variables: {
    //     to: selectedUser.username,
    //     content
    // }})
  }

  let selectedChatMarkup
  if (!messages && !messagesLoading) {
    selectedChatMarkup = <p className="info-text">Select a friend</p>
  } else if (messagesLoading) {
    selectedChatMarkup = <p className="info-text">loading...</p>
  } else if (messages.length > 0) {
    selectedChatMarkup = messages.map((message, index) => (
      <Fragment key={message.id}>
        <Message message={message} />
        {index === messages.length - 1 && (
          <div className="invisible">
            <hr className="m-0"/>
          </div>
        )}
      </Fragment>
    ))
  } else if (messages.length === 0) {
    selectedChatMarkup = <p className="info-text">You are now connected! send your first message</p>
  }

  return (
    <Col xs={10} md={8} className="p-0">
      <div>
        <Button variant="link" className="btn-light text-decoration-none"
            onClick={<Home />}>Back to home</Button>
      </div>

      <div className="messages-box d-flex flex-column-reverse mt-2 p-3">
        {selectedChatMarkup}
      </div>

      <div className="px-3 py-2 mb-3 mt-2">
        <Form onSubmit={submitMessage}>
          <Form.Group className="d-flex align-items-center m-0">
            <Form.Control type="text" className="message-input rounded-pill bg-secondary p-3 border-0"
              placeholder="Type a message..." value={content}
              onChange={(e) => setContent(e.target.value)} />
            <i className="fas fa-paper-plane fa-2x text-primary ms-2"
              role="button"
              onClick={submitMessage}></i>
          </Form.Group>
        </Form>
      </div>
    </Col>
  )
}
